package com.stacktest.nextlab.tests;

import java.text.ParseException;
import java.util.ArrayList;

import javax.xml.datatype.DatatypeConfigurationException;

import org.junit.Test;

import com.stacktest.nextlab.dto.AnalysisDto;
import com.stacktest.nextlab.dto.NotifyDto;
import com.stacktest.nextlab.dto.ResNotifyDto;
import com.stacktest.nextlab.dto.ResultDto;

public class RestNotifyTest {
	private String token = "nlsvctok";
	private int prescriptionId = 62573;
	private String analysisCode = "108";
	private String analysisHibaCode = "11227171000999117";
	private String analysisStatus = "SM";

	@Test
	public void enviarNotificacionTest() throws DatatypeConfigurationException, ParseException {
		RestConnection res = new RestConnection();
		NotifyDto nDto = new NotifyDto();
		nDto.setToken(token);
		nDto.setNumber(String.valueOf(prescriptionId));
		nDto.setBase64Pdf(getBase64PdfSample());

		nDto.setAnalyses(new ArrayList<AnalysisDto>());
		AnalysisDto aDto = new AnalysisDto();
		aDto.setCode(analysisCode);
		aDto.setHibaCode(analysisHibaCode);
		aDto.setStatus(analysisStatus);
		aDto.setDescription("Resultado completado sin alertas");
		nDto.getAnalyses().add(aDto);
		
		nDto.setAnalyses(new ArrayList<AnalysisDto>());
		AnalysisDto aDto2 = new AnalysisDto();
		aDto2.setCode(analysisCode);
		aDto2.setHibaCode(analysisHibaCode);
		aDto2.setStatus(analysisStatus);
		aDto2.setDescription("Resultado completado sin alertas");
		nDto.getAnalyses().add(aDto2);

		aDto.setResults(new ArrayList<ResultDto>());
		ResultDto rDto = new ResultDto();
		rDto.setCodigo("1232");
		rDto.setDescripcion("Descripción resultado");
		rDto.setFechaValidacion("10/10/2010 10:10:10.10");
		rDto.setFlag("N");
		rDto.setMetodo("Un método químico");
		rDto.setObservacion("Una observación");
		rDto.setResultado("Resultado del Resultado del análisis");
		rDto.setUnidad("Ml");
		rDto.setValorReferencia("Un valor de referencia");
		aDto.getResults().add(rDto);
		
		ResultDto rDto2 = new ResultDto();
		rDto2.setCodigo("222");
		rDto2.setDescripcion("Descipción resultado 2");
		rDto2.setFechaValidacion("12/12/2012 12:12:12.12");
		rDto2.setFlag("N");
		aDto.getResults().add(rDto2);
		
		res.setMensajePost(nDto);
		ResNotifyDto dto = res.ejecutar("POST", "/nextlab/notify", ResNotifyDto.class);

		assert dto != null && dto.isSuccess();
	}
	
	private String getBase64PdfSample() {
		return "";
	}
}
